
public class cinco {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// rellenar un array de numeros con for-each
		int[] numeros = new int[10];
		int count = 1;
		for (int n: numeros) {
				n = count;
				count ++;
			  System.out.println(n);
			}
	}

}
